import "../App.css";
import { useState } from "react";
import Popup from 'reactjs-popup';
import * as yup from 'yup';
import { yupResolver } from '@hookform/resolvers/yup';

export const Form = () => {
    const today: Date = new Date(Date.now());

    const [taskName, setTaskName] = useState("task");
    const [taskCompleted, setTaskCompleted] = useState(false);
    const [taskStartTime, setTaskStartTime] = useState(today.toLocaleTimeString())
    const [taskEndTime, setTaskEndTime] = useState(today.toLocaleTimeString())
    const [taskNotes, setTaskNotes] = useState("")

    const schema = yup.object().shape({
        name: yup.string().required("Task Name Required"),
        startTime: yup.string().datetime().required("Start Time is Required."),
        endTime: yup.string().datetime().required("End Time is Required."),
        notes: yup.string()
    });



    return (
        <div>
            <form className="newTaskForm">
                <input type="text" placeholder="Name of the task" onChange={(event) => {
                    setTaskName(event.target.value);
                }}></input>
                <input type="time" placeholder="Start Time" onChange={(event) => {
                    setTaskStartTime(event.target.value);
                }}></input>
                <input type="time" placeholder="End Time" onChange={(event) => {
                    setTaskEndTime(event.target.value);
                }}></input>
                <textarea placeholder="Task Notes" rows={10} cols={10} onChange={(event) => {
                    setTaskNotes(event.target.value);
                }}></textarea>
                <button className="cancelButton"> Cancel </button>
                <input className="addTaskButton" type="submit" value={"Add Task"}></input>
            </form>
        </div>
    );
}
