import { useState } from "react";
import "../App.css";
import clock_icon from '../icons/clock_icon.png';


export const Task = () => {
    const today: Date = new Date(Date.now());

    const [taskName, setTaskName] = useState("task");
    const [taskCompleted, setTaskCompleted] = useState(false);
    const [taskStartTime, setTaskStartTime] = useState(today.toLocaleTimeString())
    const [taskEndTime, setTaskEndTime] = useState(today.toLocaleTimeString())

    return (
        <div className="taskContainer">
            <input type="checkbox"></input>
            <p>Task</p>
            <p className="taskTime">
                <img src={clock_icon} alt="home icon" width="25" height="25"/>
                {taskStartTime} - {taskEndTime}
            </p>
            <button></button>
        </div>
    );
}
