import React from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { useState } from 'react';
import { Home } from './pages/Home';
import { Completed } from './pages/Completed';
import { Work } from './pages/Work';
import { Navbar } from './components/Navbar';
import { Task } from './components/Task';
import { Form } from './components/Form';
import Popup from 'reactjs-popup';

function App() {
  const [menuVisible, setMenuVisible] = useState(false);
  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  };

  return (
    <div className="App">
      <Router>

        <div className='navbar'>
          <Navbar />
        </div>

        <div className='main'>
        <Routes>
            <Route path="/" element={ <Home /> } />
            <Route path="/Completed" element={ <Completed /> } />
            <Route path="/Work" element={<Work />} />
            <Route path="*" element={<h1>ERROR: 404 Page Not Found!</h1>} />
          </Routes>

          <Task />

        <Popup trigger={<button className='newTaskButton' name='newTaskButton'> + Create Task </button>} position="center center" modal nested>
          <Form />
        </Popup>

        <div>
        <button onClick={toggleMenu}>Show Menu</button>
        {menuVisible && (
          <div className="menu-popup">
            <ul>
              <li style={{}}><button>Option 1</button></li>
              <li><button>Option 2</button></li>
              <li><button>Option 3</button></li>
            </ul>
          </div>
        )}
      </div>

        </div>
      </Router>


    </div>
  );
}

export default App;
