import React from 'react'
import { deleteTask, saveTask, getTask, updateTask, getTasks } from '../api/TaskService';
import { TaskInterface } from '../interface/TaskInterface';
import { Task } from './Task';

interface Props{
  work: boolean;
  completed: boolean;
}

const TaskList = async (props: Props) => {
  const { data } = await getTasks();

  if(props.work){
    return (
      <>
        {data?.content?.length > 0 && data.map((currentTask: TaskInterface) => {
          return <Task task={currentTask} />
        })};
      </>
    )
  }
  if(props.completed){
    return (
      <>
        {data?.content?.length > 0 && data.map((currentTask: TaskInterface) => {
          return <Task task={currentTask} />
        })}
      </>
    )
  }
