import { useState } from "react";
import "../App.css";
import clock_icon from '../icons/clock_icon.png';
import Popup from 'reactjs-popup';
import { deleteTask, saveTask, getTask, updateTask, getTasks } from '../api/TaskService';
import { TaskInterface } from "../interface/TaskInterface";


export const Task = ({ task } : {task: TaskInterface}) => {
    const [menuVisible, setMenuVisible] = useState(false);
    const toggleMenu = () => {
        setMenuVisible(!menuVisible);
    };

    const deleteTask = async (id: string) => {
        try{
          await deleteTask(id);
          window.location.reload(false);
        }
        catch(error){
          console.log(error);
        }
      }


    return (
        <div className="taskContainer">
            {task.taskCompleted ? 
            (<input  type="checkbox" checked onChange={async () =>  {
                task.taskCompleted = false;
                try{
                    await saveTask({...task,taskCompleted: false});
                }
                catch(error){
                    console.log(error);
                }
            }} ></input>) 
            : <input  type="checkbox"  onChange={async () =>  {
                task.taskCompleted = true;
                try{
                    await saveTask({...task,taskCompleted: true});
                }
                catch(error){
                    console.log(error);
                }
            }} ></input>}
            <p>{task.name}</p>
            <p className="taskTime">
                <img src={clock_icon} alt="home icon" width="25" height="25"/>
                {task.startTime} - {task.endTime}
            </p>

            <Popup trigger={<button className="toggleButton"></button>} position="bottom center">
                <div className="menuPopup">
                    <ul>
                    <li className='listItem'><button className="listButton">Edit</button></li>
                    <li className='listItem' ><button className="listButton">Delete</button></li>
                    </ul>
                </div>
            </Popup>

        </div>
    );
}
