import React, { createRef } from 'react';
import { useRef } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { useState } from 'react';
import { Home } from './pages/Home';
import { Completed } from './pages/Completed';
import { Work } from './pages/Work';
import { Navbar } from './components/Navbar';
import { Task } from './components/Task';
import { Form } from './components/Form';
import Popup from 'reactjs-popup';
import { deleteTask, saveTask, getTask, updateTask, getTasks } from './api/TaskService';
import { TaskInterface } from './interface/TaskInterface';

function App() {

  const Home = () => {
    const work: boolean = false;
    const completed: boolean = false;

    interface Props{
        work: boolean;
        completed: boolean;
      }

      const TaskList = async (props: Props) => {
        const { data } = await getTasks();

        if(props.work){
          return (
            <>
              {data?.content?.length > 0 && data.map((currentTask: TaskInterface, key:any) => {
                if(!currentTask.taskCompleted){
                  return <Task task={currentTask} key={key}/>
                }
              })};
            </>
          )
        }
        if(props.completed){
          return (
            <>
              {data?.content?.length > 0 && data.map((currentTask: TaskInterface, key:any) => {
                if(currentTask.taskCompleted){
                  return <Task task={currentTask} key={key} />
                }
              })}
            </>
          )
        }
          return (
          <>
            {data?.content?.length > 0 && data.map((currentTask: TaskInterface, key:any) => {
              return(
                <Task task={currentTask} key={key} />
              ); 
            })}
          </>
        )}
}

  const modalRef = React.createRef<HTMLDialogElement>();
  const toggleModal = (show:boolean) => { show ? modalRef?.current?.showModal()  : modalRef?.current?.close();};

  return (
    <div className="App">
      <Router>

        <div className='navbar'>
          <Navbar />
        </div>

        <div className='main'>
        <Routes>
            <Route path="/" element={ <Home /> } />
            <Route path="/Completed" element={ <Completed /> } />
            <Route path="/Work" element={<Work />} />
            <Route path="*" element={<h1>ERROR: 404 Page Not Found!</h1>} />
          </Routes>

          <button onClick={() => toggleModal(true)} className='newTaskButton' name='newTaskButton'> + Create Task </button>
          <dialog ref={modalRef}>
            <Form toggleModal={toggleModal} />
          </dialog>



        </div>
      </Router>




    </div>
  );
}

export default App;
