import React from 'react'
import { deleteTask, saveTask, getTask, updateTask, getTasks } from '../api/TaskService';
import { TaskInterface } from '../interface/TaskInterface';
import { Task } from './Task';

interface Props{
  work: boolean;
  completed: boolean;
  data: any;
}

const TaskList = (props: Props) => {

  if(props.work){
    return (
      <>
        {props.data?.length > 0 && props.data.map((currentTask: TaskInterface, key:any) => {
          if(!currentTask.completed){
            return <Task task={currentTask} key={key}/>
          }
        })};
      </>
    )
  }
  if(props.completed){
    return (
      <>
        {props.data?.length > 0 && props.data.map((currentTask: TaskInterface, key:any) => {
          if(currentTask.completed){
            return <Task task={currentTask} key={key} />
          }
        })}
      </>
    )
  }
    return (
    <>
      {props.data?.length > 0 && props.data.map((currentTask: TaskInterface, key:any) => {
        return(
          <Task task={currentTask} key={key} />
        ); 
      })}
    </>
  )}

export default TaskList
