import { useState } from "react";
import "../App.css";
import clock_icon from '../icons/clock_icon.png';
import Popup from 'reactjs-popup';
import { deleteTask, saveTask, getTask, updateTask, getTasks } from '../api/TaskService';


export const Task = ({ id }) => {
    const [menuVisible, setMenuVisible] = useState(false);
    const [task, setNewTask] = useState({
        id: id,
        name: "",
        startTime: "",
        endTime: "",
        taskCompleted: false,
        notes: "",
    });
    const toggleMenu = () => {
        setMenuVisible(!menuVisible);
    };

    const today: Date = new Date(Date.now());

    const [taskName, setTaskName] = useState("task");
    const [taskCompleted, setTaskCompleted] = useState(false);
    const [taskStartTime, setTaskStartTime] = useState(today.toLocaleTimeString())
    const [taskEndTime, setTaskEndTime] = useState(today.toLocaleTimeString())

    return (
        <div className="taskContainer">
            {taskCompleted ? 
            (<input  type="checkbox" checked onChange={() => setTaskCompleted(false)} ></input>) 
            : <input  type="checkbox" onChange={() => setTaskCompleted(true)} ></input>}
            <p>{taskName}</p>
            <p className="taskTime">
                <img src={clock_icon} alt="home icon" width="25" height="25"/>
                {taskStartTime} - {taskEndTime}
            </p>

            <Popup trigger={<button className="toggleButton"></button>} position="bottom center">
                <div className="menuPopup">
                    <ul>
                    <li className='listItem'><button className="listButton">Edit</button></li>
                    <li className='listItem'><button className="listButton">Delete</button></li>
                    </ul>
                </div>
            </Popup>

        </div>
    );
}
